mapper.select:
select c.id,c.name,c.company,c.position,c.mobile,c.email,staff.name follow_name from (select id,`name`,company,`position`,mobile,email from customer
AND customer.name LIKE #{keyword}
AND customer.`update_time` >= #{startTime}
AND customer.`update_time` <= #{endTime}
ORDER BY ${orderColumn} ${orderType} limit ${start},${length}) c LEFT JOIN staff_customer_follow_relation ON c.id = staff_customer_follow_relation.customer_id AND staff_customer_follow_relation.is_follow = 1 LEFT JOIN staff ON staff.userid = staff_customer_follow_relation.user_id

select COUNT(DISTINCT (c.id))
from
(select id,`name`,company,`position`,mobile,email
from customer


    AND customer.name LIKE #{keyword}


    AND customer.`update_time` >= #{startTime}


    AND customer.`update_time` <= #{endTime}


) c
LEFT JOIN staff_customer_follow_relation
ON c.id = staff_customer_follow_relation.customer_id AND staff_customer_follow_relation.is_follow = 1
LEFT JOIN staff
ON staff.userid = staff_customer_follow_relation.user_id

select count(*)
from customer
where mobile = #{mobile}
