mapper.select:
select id,share_id,user_id,customer_id,message_id,open_id,times,view_time
from customer_readinfo
where share_id = #{shareId} and message_id = #{messageId}

SELECT cr.id,cr.share_id,cr.message_id,message.msgType,cr.user_id,cr.customer_id,customer.`name`,cr.times,cr.city,cr.open_time,cr.view_time,cr.page_count,cr.total_time,cr.read_info,cr.update_time
from (
select id,share_id,message_id,user_id,customer_id,times,city,open_time,view_time,page_count,total_time,read_info,update_time
from customer_readinfo
where message_id = #{messageId} and user_id = #{userId}
) cr
left join message
on cr.message_id = message.id
left join customer
on cr.customer_id = customer.id;
