mapper.delete:
delete from group_staff_relation
where group_id = #{groupId,jdbcType=INTEGER}
  and staff_id = #{staffId,jdbcType=INTEGER}

mapper.insert:
insert into group_staff_relation (group_id, staff_id, def_group
  )
values (#{groupId,jdbcType=INTEGER}, #{staffId,jdbcType=INTEGER}, #{defGroup,jdbcType=INTEGER}
  )

mapper.update:
update group_staff_relation
set def_group = #{defGroup,jdbcType=INTEGER}
where group_id = #{groupId,jdbcType=INTEGER}
  and staff_id = #{staffId,jdbcType=INTEGER}

mapper.select:
select group_id, staff_id, def_group
from group_staff_relation
where group_id = #{groupId,jdbcType=INTEGER}
  and staff_id = #{staffId,jdbcType=INTEGER}

select staff_id
from group_staff_relation
where group_id = #{groupId}

select staff_id
from group_staff_relation
where group_id = #{groupId}
