mapper.select:
select id,title,description,picurl
from message
where third_param_id = #{thirdParamId,jdbcType=VARCHAR }

select id,title from message
message.delFlag = 0
AND message.title LIKE #{keyword}

SELECT COUNT(*) FROM message
LEFT JOIN staff
ON message.create_user_id = staff.userid

  message.delFlag = 0

    AND CONCAT(`title`,staff.`name`) LIKE #{keyword}


    AND message.`update_time` >= #{startTime}


    AND message.`update_time` <= #{endTime}

SELECT message.id,corp_id,suite_id,corpid,title,msgType,create_user_id,`name`,message.update_time,status
FROM message
LEFT JOIN staff
ON message.create_user_id = staff.userid

  message.delFlag = 0

    AND CONCAT(message.`title`,staff.`name`) LIKE #{keyword}


    AND message.`update_time` >= #{startTime}


    AND message.`update_time` <= #{endTime}


ORDER BY ${orderColumn} ${orderType}
limit ${start},${length}

mapper.update:
update message
set status = 0
where id = #{id}

update message
set status = 1
where id = #{id}

mapper.select:
select distinct(id),group_id,open_count,msgType,titleText,staff_name,update_time
from v_message_opencount_time_tag

  group_id = #{groupId}

    AND tag_id = #{tagId}


order by ${order} ${orderType}
limit ${start},${size}
